import React, { Component } from "react";
import { Button, Table, Icon } from "semantic-ui-react";
import Layout from "../../components/Layout";
import MailRow from "../../components/List/MailRow";
import { Link } from "react-router-dom";
const web3 = require("../../ethereum/test");
const Mail = require('../../ethereum/build/Mail.json');

class mailList extends Component {
  mailButton = () => {
    return this.props.history.push(window.location.pathname+'/addMail');
  }

  getMail = async () => {
      const mail = new web3.eth.Contract(JSON.parse(Mail.interface),req.body.address);
      const senderInfos = await mail.methods.senderInfos(0).call();
      const receiverInfos = await mail.methods.receiverInfos(0).call();
      const mailInfos = await mail.methods.mailInfos(0).call();
      console.log(senderInfos);
      console.log(receiverInfos);
      console.log(mailInfos);
  }

  renderRow() {
    return (
      <MailRow

        //key={index} 같이 값 넣기
      />
    );
  }


  render() {
    const { Header, Row, HeaderCell, Body } = Table;
    return (
      <Layout>
          <Button icon labelPosition='left' onClick={this.mailButton} basic color="purple" floated="left" style={{marginBottom: '10px'}}>
            <Icon name='add' />
            택배 접수
          </Button>

        <Table >
          <Header>
            <Row>
              <HeaderCell style={{minWidth: "6em"}}>받는 사람</HeaderCell>
              <HeaderCell >주소</HeaderCell>
              <HeaderCell style={{minWidth: "9em"}}>연락처</HeaderCell>
              <HeaderCell style={{minWidth: "6em"}}>상품명</HeaderCell>
              <HeaderCell style={{minWidth: "6em"}}>배송 상태</HeaderCell>
              <HeaderCell style={{minWidth: "7em"}}>QR코드</HeaderCell>
              <HeaderCell style={{minWidth: "7em"}}>상세 정보</HeaderCell>
            </Row>
          </Header>
          <Body>{this.renderRow()}</Body>
        </Table>
      </Layout>
    );
  }
}

export default mailList;
