import React, { Component } from 'react';
import Layout from '../components/Layout';
import { Button, Form, Grid, Header, Image, Message, Segment } from 'semantic-ui-react';
import factory from '../ethereum/factory';
import {Link} from 'react-router-dom';

class Login extends Component {

  static async getInitialProps(){ //157lecture
    const mails = await factory.methods.getDeployedMails().call();

    return { mails: mails};
  }

  state = {
    users: ""
  }


  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ users: res }))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch("/api/users");
    const body = await response.json();
    return body;
  };

  handleChange(e) {
    let nextState = {};
    nextState[e.target.name] = e.target.value;
    this.setState(nextState);
  }

  handleSubmit(event) {
    e.preventDefault();
    this.makeAddress();
    this.addUser().then(response => {
      console.log(response.data);
    });
    this.setState({
      id: "",
      pw: ""
    });
    //window.location.reload();
    }

  loginCheck = () => {
    const length = this.state.users.length;
    console.log(this.state.id);
    console.log(this.state.pw);

    let login_state = 0;
    var i = 0;
    for(i;i<length;i++)
    {
      if(this.state.users[i].id==this.state.id)
      {
        console.log("id success");
        login_state=1;
        break;
      }
      else {
        console.log("id fail");
      }
    }

    console.log(i);

    if(login_state == 1)
    {
      if(this.state.users[i].pw==this.state.pw)
      {
        console.log("pw success");
        alert("로그인 성공");
      }
      else {
        console.log("pw fail");
        alert("로그인 실패");
      }
    }

    else{
      alert("로그인 실패");
    }


  }

  render(){
    return(
      <Layout>
        <Grid textAlign='center' style={{ height: '40vh' }} verticalAlign='middle'>
          <Grid.Column style={{ maxWidth: 450 }}>
            <Header as='h2' color='teal' textAlign='center'>
               귀하의 계정을 로그인해주세요
              </Header>
              <Form size='large' onSubmit={this.handleSubmit}>
              <Segment stacked>
                <Form.Input
                  fluid icon='user'
                  iconPosition='left'
                  placeholder='아이디'
                  value ={this.state.id}
                  onChange={this.handleChange}
                />
                <Form.Input
                  fluid
                  icon='lock'
                  iconPosition='left'
                  placeholder='비밀번호'
                  type='password'
                  value ={this.state.pw}
                  onChange={this.handleChange}
                />

                <Button color='teal' fluid size='large' onClick = {this.loginCheck}>
                  로그인
                </Button>
              </Segment>
            </Form>
            <Message>
              처음이십니까?
              <Link to="/signUp"> 회원가입</Link>
            </Message>
            <Message>
                <Link to="/addMail">택배 등록</Link>
            </Message>
            <Message>
                <Link to="/list">회원 목록</Link>
            </Message>
          </Grid.Column>
        </Grid>
      </Layout>
    );
  }
}

export default Login;
