import React, { Component } from 'react';
import Layout from '../components/Layout';
import { Button, Form, Grid, Header, Image, Message, Segment } from 'semantic-ui-react';
import factory from '../ethereum/factory';
import {Link} from 'react-router-dom';

let user_data;

class Login extends Component {

  static async getInitialProps(){ //157lecture
    const mails = await factory.methods.getDeployedMails().call();

    return { mails: mails};
  }

  state = {
    users: ""
  }


  callApi = async () => {
    const response = await fetch("/api/users");
    const body = await response.json();
    user_data = JSON.parse(JSON.stringify(body));
    return body;
  }

  handleOnClick = () => {
  //  alert("click");
    this.callApi()
      .then(res => this.setState({ users: res }))
      .catch(err => console.log(err));
      console.log(this.state.users.length);
    //this.loginCheck();
    alert("click");
  }

  loginCheck = () => {
    let id = 'apple';
    for(var i=0;i<user_data;i++)
    {
      if(user_data[i].id===id)
      {
        console.log("success");
      }
      else {
        console.log("fail");
      }
    }

  }

  render(){
    return(
      <Layout>
        <Grid textAlign='center' style={{ height: '40vh' }} verticalAlign='middle'>
          <Grid.Column style={{ maxWidth: 450 }}>
            <Header as='h2' color='teal' textAlign='center'>
               귀하의 계정을 로그인해주세요
              </Header>
              <Form size='large'>
              <Segment stacked>
                <Form.Input fluid icon='user' iconPosition='left' placeholder='아이디' />
                <Form.Input
                  fluid
                  icon='lock'
                  iconPosition='left'
                  placeholder='비밀번호'
                  type='password'
                />

                <Button color='teal' fluid size='large' onClick = {this.handleOnClick}>
                  로그인
                </Button>
              </Segment>
            </Form>
            <Message>
              처음이십니까?
              <Link to="/signUp"> 회원가입</Link>
            </Message>
            <Message>
                <Link to="/addMail">택배 등록</Link>
            </Message>
            <Message>
                <Link to="/list">회원 목록</Link>
            </Message>
          </Grid.Column>
        </Grid>
      </Layout>
    );
  }
}

export default Login;
